# coding: utf-8from bs4 import BeautifulSoupimport osimport sysdef xml_parse(filename):    soup = BeautifulSoup(open(filename, encoding="utf-8"), "lxml")    with open(filename, 'w', encoding="utf-8") as f:        for doc in soup.find_all(site="1"):            f.write(doc.prettify())        for doc in soup.find_all(site="transorg1"):            f.write(doc.prettify())if __name__ == "__main__":    # 一个source 对应着4个ref,因此需要去重    # directory = "/tcxia/nmt_data/cwmt/multi-reference/"    # directory = 'D:\data\\nmt\cwmt\multi-reference'    directory = sys.argv[1]    for dirpath, dirnames, filenames in os.walk(directory):        for filename in filenames:            if "ref" in filename:                xml_parse(os.path.join(dirpath, filename))    # xml_parse(filename)